query BallByBallQuery($key: String!, $overKey: String) {
  cricket_match_ball_by_ball(match_key: $key, over_key: $overKey) {
    over {
      index {
        innings
        over_number
        __typename
      }
      balls {
        key
        ball_type
        batting_team
        comment
        innings
        overs
        batsman {
          player_key
          ball_count
          runs
          is_dot_ball
          is_four
          is_six
          __typename
        }
        bowler {
          player_key
          ball_count
          runs
          extras
          is_wicket
          __typename
        }
        team_score {
          ball_count
          runs
          extras
          is_wicket
          __typename
        }
        fielders {
          player_key
          is_run_out
          is_stumps
          is_catch
          is_assists
          __typename
        }
        wicket {
          player_key
          wicket_type
          __typename
        }
        non_striker_key
        entry_time
        ball_play_status
        ball_tags
        updated_time
        __typename
      }
      __typename
    }
    previous_over_index {
      innings
      over_number
      __typename
    }
    next_over_index {
      innings
      over_number
      __typename
    }
    previous_over_key
    next_over_key
    __typename
  }
}
