query:
query FeaturedMatchQuery($tournamentKey: String, $curationKey: String) {
  cricket_featured_matches(
    tournament_key: $tournamentKey
    curation_key: $curationKey
  ) {
    matches {
      key
      name
      short_name
      sub_title
      status
      start_at
      tournament {
        key
        name
        short_name
        __typename
      }
      metric_group
      sport
      winner
      teams {
        key
        value {
          key
          code
          name
          country_code
          __typename
        }
        __typename
      }
      venue {
        key
        name
        city
        country {
          short_code
          code
          name
          official_name
          is_region
          __typename
        }
        __typename
      }
      association {
        key
        code
        name
        country {
          short_code
          code
          name
          official_name
          is_region
          __typename
        }
        parent
        __typename
      }
      messages
      gender
      format
      __typename
    }
    intelligent_order
    __typename
  }
}
